mapper.select:
	SELECT * FROM(
		SELECT A.*
		,COUNT(*) OVER() AS PRODUCT_TOTAL_CNT

		FROM (
		
		WHERE REGT_ID = #{regtId}
		
			AND PRODUCT_ID = #{productId}
		
		ORDER BY PRODUCT_ID ASC
		 )A
		)    A WHERE ROWNUM = 1

		
			SELECT * FROM
			(
				SELECT A.*,B.DUP_CNT FROM TB_PRODUCT A,
				(
					SELECT A.PARNT_ID,COUNT(A.CATE_ID) AS DUP_CNT FROM (
					SELECT PARNT_ID,CATE_ID FROM TB_MAPPING_CATE WHERE CATE_TYPE = '001'
					) A, (SELECT PARNT_ID AS CATE_PARNT_ID, CATE_ID FROM TB_MAPPING_CATE WHERE PARNT_ID =#{productCateId}) B
					WHERE A.CATE_ID = B.CATE_ID  GROUP BY A.PARNT_ID
				) B WHERE A.PRODUCT_ID = B.PARNT_ID AND B.DUP_CNT > 0 ORDER BY B.DUP_CNT DESC
			)
			
				
				 	
					AND MSRP >= #{msrpMin}
					
				
				
					
					AND MSRP_MAX <= #{msrpMax}
					
				

				
				 	
					AND COST >= #{costMin}
					
				
				
					
					
					AND COST <= #{costMax}
					
				
				
				 	
					AND MARGIN >= #{marginMin}
					
				
				
					
					AND MARGIN_MAX <= #{marginMax}

mapper.update:
