UPDATE BLENDING.TB_PRODUCT
	SET
	 PRODUCT_REG_STATUS	=#{productRegStatus}
	,MARKETING_BUDGET  	=#{marketingBudget}
	,MV_URL				=#{mvUrl}
	,UPDT_ID           = #{regtId}
	,UPDT_DT           = sysdate
WHERE PRODUCT_ID 	   	=#{productId}

		INSERT INTO
				BLENDING.TB_CATEGORY
			(
				 CATE_ID
				,CATE_NM
				)
			VALUES(
				 (SELECT 'ctg' || TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') || LPAD(NVL(Max(SUBSTR(CATE_ID,17))+1,1),3,'0') AS PRODUCT_ID FROM TB_CATEGORY
		            WHERE TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') = SUBSTR(CATE_ID,4,14)
				 )
				,#{cateNm}

			)
