mapper.update:
UPDATE BLENDING.TB_USER
	SET
		 FIRST_NM 		= #{firstNm}
		,LAST_NM 		= #{lastNm}
		,USER_TYPE 		= #{userType}
		,EMAIL 			= #{email}
		,EMAIL_CHECK_YN = #{emailCheckYn}
		,PHONE_CHECK_YN = #{phoneCheckYn}
		,POSITION 		= #{position}
		,UPDT_DT 		= SYSDATE
WHERE USER_ID 		= #{userId}
